import { Dict, StateMachine } from './types.js';

/**
 * Delay can be specified as:
 * - a string (reference to `options.delays`)
 * - a number (in ms)
 * - a function that returns a number (in ms)
 *
 * Let's resolve this to a number
 */
declare function determineDelayFn<TContext extends Dict, TEvent extends StateMachine.EventObject>(delay: StateMachine.Delay<TContext, TEvent> | undefined, delaysMap: StateMachine.DelayMap<TContext, TEvent> | undefined): (context: TContext, event: TEvent) => number | undefined;

export { determineDelayFn };
